","keccak256":"0x9772845c886f87a3aab315f8d6b68aa599027c20f441b131cd4afaf65b588900","license":"MIT"},"contracts/AuctionHouse.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./Market.sol";

contract AuctionHouse is Market {
    enum AuctionStatus { None, Created, Cancelled, Bidding, Completed }
    struct Auction {
        address creator;
        address bidder;
        Item item;
        uint minPrice;
        uint currentPrice;
        uint expiration;
        uint deadline;
        uint commission;
        AuctionStatus status;
    }

    uint public tickSize;
    uint public minExpiration;
    uint public maxExpiration;

    uint public numAuctions;
    mapping(uint => Auction) public auctions;

    event Created(uint indexed id, address indexed creator);
    event MinPriceChanged(uint indexed id);
    event Cancelled(uint indexed id);
    event NewBidder(uint indexed id, address bidder);
    event Completed(uint indexed id, address winner);

    constructor(
        address _money,
        uint _commission,
        uint _tickSize
    )
        Market(_money, _commission)
    {
        tickSize = _tickSize;
        minExpiration = 1 hours;
        maxExpiration = 10 days;
    }

    function create(
        Item calldata _item,
        uint _minPrice,
        uint _expiration
    )
        external
        whenNotPaused
        returns (uint id)
    {
        uint minPrice = roundDownPrice(_minPrice);
        require(minPrice > 0, "AuctionHouse: zero minPrice");
        require(
            _expiration >= minExpiration && _expiration <= maxExpiration,
            "AuctionHouse: invalid expiration"
        );

        address creator = msg.sender;
        transferItem(creator, address(this), _item);
        id = ++numAuctions;
        auctions[id] = Auction({
            creator: creator,
            bidder: address(0),
            item: _item,
            minPrice: minPrice,
            currentPrice: 0,
            expiration: _expiration,
            deadline: 0,
            commission: commission,
            status: AuctionStatus.Created
        });
        emit Created(id, creator);
    }

    function cancel(uint _id) external {
        Auction memory auction = auctions[_id];
        require(auction.status == AuctionStatus.Created, "AuctionHouse: invalid auction status");
        address creator = msg.sender;
        require(auction.creator == creator, "AuctionHouse: not creator");
        transferItem(address(this), creator, auction.item);
        auctions[_id].status = AuctionStatus.Cancelled;
        emit Cancelled(_id);
    }

    function changeMinPrice(uint _id, uint _minPrice) external {
        uint minPrice = roundDownPrice(_minPrice);
        require(minPrice > 0, "AuctionHouse: zero minPrice");
        address creator = msg.sender;
        Auction memory auction = auctions[_id];
        require(auction.status == AuctionStatus.Created, "AuctionHouse: invalid offer status");
        require(auction.creator == creator, "AuctionHouse: not creator");
        auctions[_id].minPrice = minPrice;
        emit MinPriceChanged(_id);
    }

    function bid(uint256 _id, uint256 _price, address _beneficiary) external {
        require(isAuctionActive(_id), "AuctionHouse: auction not active");
        Auction memory auction = auctions[_id];
        address committer = msg.sender;
        address bidder = msg.sender;
        if (_beneficiary != address(0)) {
            bidder = _beneficiary;
        }

        uint price = roundDownPrice(_price);
        if (auction.status == AuctionStatus.Created) {
            require(price >= auction.minPrice, "AuctionHouse: invalid price");
        } else {
            require(price > auction.currentPrice, "AuctionHouse: invalid price");
            transferMoney(address(this), auction.bidder, auction.currentPrice);
        }

        transferMoney(committer, address(this), price);
        auctions[_id].bidder = bidder;
        auctions[_id].currentPrice = price;
        auctions[_id].deadline = block.timestamp + auction.expiration;
        auctions[_id].status = AuctionStatus.Bidding;
        emit NewBidder(_id, bidder);
    }

    function complete(uint _id) external {
        require(isAuctionEnd(_id), "AuctionHouse: auction not end");
        Auction memory auction = auctions[_id];
        uint finalPrice = auction.currentPrice;
        uint fee = finalPrice * auction.commission / 10000;
        transferMoney(address(this), owner(), fee);
        transferMoney(address(this), auction.creator, finalPrice - fee);
        transferItem(address(this), auction.bidder, auction.item);
        auctions[_id].status = AuctionStatus.Completed;
        emit Completed(_id, auction.bidder);
    }

    function setTickSize(uint _tickSize) external onlyOwner {
        tickSize = _tickSize;
    }

    function setMinExpiration(uint _minExpiration) external onlyOwner {
        minExpiration = _minExpiration;
    }

    function setMaxExpiration(uint _maxExpiration) external onlyOwner {
        maxExpiration = _maxExpiration;
    }

    function isAuctionActive(uint _id) public view returns (bool) {
        Auction memory auction = auctions[_id];
        return (auction.status == AuctionStatus.Created) ||
            (auction.status == AuctionStatus.Bidding && block.timestamp <= auction.deadline);
    }

    function isAuctionEnd(uint _id) public view returns (bool) {
        Auction memory auction = auctions[_id];
        return auction.status == AuctionStatus.Bidding && block.timestamp > auction.deadline;
    }

    function roundDownPrice(uint _price) private view returns (uint) {
        return _price / tickSize * tickSize;
    }
}
","keccak256":"0x7299104b96d492aad056e1c797b88e8d2d874a8175e0358a902d9346e5e90e38","license":"MIT"},"contracts/HeroBank.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./AuctionHouse.sol";

contract HeroBank is Ownable, Pausable, IERC721Receiver, ERC1155Holder {
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.UintSet;

    address public immutable DFKHero;
    uint public immutable MIN_COLLATERAL;
    uint public constant BORROW_LIMIT = 6;

    AuctionHouse public auctionHouse;
    uint public auctionExpiration;

    address public operator;

    uint public maxHeroCount;
    // current hero count in bank
    uint numHeroes;
    // heroId to hero owner address
    mapping(uint256 => address) public heroOwners;
    // owner address to heroId set
    mapping(address => EnumerableSet.UintSet) ownedHeroes;

    uint numBorrows;
    uint[BORROW_LIMIT] borrowedHeroes;

    // Mapping from user address to borrow count
    mapping(address => uint) public userLoans;
    uint public totalLoans;

    event CollateralReceived(address sender, uint256 value);
    event HeroReceived(address indexed from, uint256 tokenId);
    event HeroSent(address indexed to, uint256 tokenId);
    event OperatorChanged(address newOperator, address oldOperator);
    event Claim(address indexed user, uint256 userLoans, uint256 totalLoans);

    constructor(
        uint _maxHeroCount,
        uint _minCollateral,
        address _auctionHouse,
        address _DFKHero
    ) {
        maxHeroCount = _maxHeroCount;
        MIN_COLLATERAL = _minCollateral;
        auctionHouse = AuctionHouse(_auctionHouse);
        auctionExpiration = 1 days;
        DFKHero = _DFKHero;
    }

    modifier onlyOperator() {
        require(operator == msg.sender, "HeroBank: not operator");
        _;
    }

    receive() external payable {
        emit CollateralReceived(msg.sender, msg.value);
    }

    function onERC721Received(
        address /* _operator */,
        address _from,
        uint256 _tokenId,
        bytes calldata /* _data */
    )
        external
        override
        whenNotPaused
        returns (bytes4)
    {
        if (msg.sender == DFKHero) {
            assert(heroOwners[_tokenId] == address(0));
            require(numHeroes <= maxHeroCount, "HeroBank: exceed maxHeroCount");
            onHeroReceived(_tokenId, _from);
        }
        return this.onERC721Received.selector;
    }

    function withdrawalHeroes(uint[] calldata _heroes) external {
        for (uint i = 0; i < _heroes.length; i++) {
            uint heroId = _heroes[i];
            address heroOwner = heroOwners[heroId];
            require(msg.sender == heroOwner, "HeroBank: not hero owner");
            IERC721(DFKHero).safeTransferFrom(address(this), heroOwner, heroId);
            onHeroSent(heroId, heroOwner);
        }
    }

    function createAuctionAndBid(Item calldata _item, uint256 _initialPrice) external {
        transferMoney(msg.sender, address(this), _initialPrice);
        uint auctionId = auctionHouse.create(_item, _initialPrice, auctionExpiration);
        auctionHouse.money().approve(address(auctionHouse), _initialPrice);
        auctionHouse.bid(auctionId, _initialPrice, msg.sender);
    }

    function getClaimable(address user) public view returns (uint256) {
        return getBalance() * userLoans[user] / totalLoans;
    }

    function claim() external {
        uint claimable = getClaimable(msg.sender);
        require(claimable > 0, "HeroBank: no claimable");
        uint count = userLoans[msg.sender];
        userLoans[msg.sender] = 0;
        totalLoans -= count;
        transferMoney(address(this), msg.sender, claimable);
        emit Claim(msg.sender, count, totalLoans);
    }

    function borrowHeroes(uint[] memory _heroes) external onlyOperator {
        require(numBorrows == 0, "HeroBank: unreturned heroes");
        require(_heroes.length <= BORROW_LIMIT, "HeroBank: exceed BORROW_LIMIT");
        require(address(this).balance >= MIN_COLLATERAL, "HeroBank: not enough margin");
        for (uint i = 0; i < _heroes.length; i++) {
            borrowedHeroes[i] = _heroes[i];
            IERC721(DFKHero).transferFrom(address(this), operator, _heroes[i]);
        }
        numBorrows = _heroes.length;
    }

    function repayHeroes() external onlyOperator {
        require(numBorrows > 0, "HeroBank: no borrowed hero");
        for (uint i = 0; i < numBorrows; i++) {
            uint heroId = borrowedHeroes[i];
            IERC721(DFKHero).transferFrom(operator, address(this), heroId);
            address heroOwner = heroOwners[heroId];
            userLoans[heroOwner] += 1;
            totalLoans += 1;
        }
        numBorrows = 0;
    }

    function withdrawalCollateral() external onlyOwner {
        require(numBorrows == 0, "HeroBank: unreturned heroes");
        Address.sendValue(payable(owner()), address(this).balance);
    }

    function setHeroOwner(uint _heroId, address _owner) external onlyOwner {
        require(IERC721(DFKHero).ownerOf(_heroId) == address(this), "HeroBank: not found");
        require(heroOwners[_heroId] == address(0), "HeroBank: already owned");
        onHeroReceived(_heroId, _owner);
    }

    function setAuctionExpiration(uint _expiration) external onlyOwner {
        auctionExpiration = _expiration;
    }

    function setMaxHeroCount(uint _maxHeroCount) external onlyOwner {
        maxHeroCount = _maxHeroCount;
    }

    function setOperator(address _operator) external onlyOwner {
        address oldOperator = operator;
        operator = _operator;
        emit OperatorChanged(operator, oldOperator);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function approveItem(
        address _itemAddr,
        ItemType _itemType,
        bool _approved
    )
        external
        onlyOwner
    {
        if (_itemType == ItemType.ERC20) {
            uint amount = _approved ? type(uint).max : 0;
            IERC20(_itemAddr).approve(address(auctionHouse), amount);
        } else if (_itemType == ItemType.ERC721) {
            IERC721(_itemAddr).setApprovalForAll(address(auctionHouse), _approved);
        } else if (_itemType == ItemType.ERC1155) {
            IERC1155(_itemAddr).setApprovalForAll(address(auctionHouse), _approved);
        }
    }

    function getBorrowedHeroes() public view returns (uint[] memory) {
        uint[] memory result = new uint[](numBorrows);
        for (uint i = 0; i < numBorrows; i++) {
            result[i] = borrowedHeroes[i];
        }
        return result;
    }

    function getHeroesOfOwner(address _owner) public view returns (uint[] memory) {
        EnumerableSet.UintSet storage heroes = ownedHeroes[_owner];
        uint length = heroes.length();
        uint[] memory result = new uint[](length);
        for (uint i = 0; i < length; i++) {
            result[i] = heroes.at(i);
        }
        return result;
    }

    function getBalance() public view returns (uint) {
        return auctionHouse.money().balanceOf(address(this));
    }

    function onHeroReceived(uint _heroId, address _owner) private {
        heroOwners[_heroId] = _owner;
        ownedHeroes[_owner].add(_heroId);
        numHeroes++;
        emit HeroReceived(_owner, _heroId);
    }

    function onHeroSent(uint _heroId, address _owner) private {
        delete heroOwners[_heroId];
        ownedHeroes[_owner].remove(_heroId);
        numHeroes--;
        emit HeroSent(_owner, _heroId);
    }

    function transferMoney(address _from, address _to, uint _amount) private {
        if (_from == address(this)) {
            auctionHouse.money().safeTransfer(_to, _amount);
        } else {
            auctionHouse.money().safeTransferFrom(_from, _to, _amount);
        }
    }
}
","keccak256":"0x45929ef6ce1b3aa5dbec745eefbb8a06b6999c29795beafd774869faf81bb6a8","license":"MIT"},"contracts/Market.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

enum ItemType { ERC20, ERC721, ERC1155 }
struct Item {
    ItemType itemType;
    address addr;
    uint[] amounts;
    uint[] ids;
}

contract Market is Ownable, Pausable, ERC721Holder, ERC1155Holder {
    using SafeERC20 for IERC20;

    IERC20 public immutable money;

    uint public commission; // in bps

    event CommissionChanged(uint newCommission, uint oldCommission);

    constructor(address _money, uint _commission) {
        money = IERC20(_money);
        commission = _commission;
    }

    function setComission(uint _commission) external onlyOwner {
        uint oldCommission = commission;
        commission = _commission;
        emit CommissionChanged(commission, oldCommission);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function transferItem(address _from, address _to, Item memory _item) internal {
        if (_item.itemType == ItemType.ERC721) {
            require(_item.ids.length > 0, "Market: zero ids.length");
            for (uint i = 0; i < _item.ids.length; i++) {
                IERC721(_item.addr).safeTransferFrom(_from, _to, _item.ids[i]);
            }
        } else if (_item.itemType == ItemType.ERC1155) {
            require(_item.ids.length > 0, "Market: zero ids.length");
            require(
                _item.ids.length == _item.amounts.length,
                "Market: ids.length and amounts.length are not equal"
            );
            for (uint i = 0; i < _item.ids.length; i++) {
                require(_item.amounts[i] > 0, "Market: zero amount");
                IERC1155(_item.addr).safeTransferFrom(_from, _to, _item.ids[i], _item.amounts[i], "");
            }
        } else if (_item.itemType == ItemType.ERC20) {
            require(_item.amounts.length > 0, "Market: zero amounts.length");
            require(_item.amounts[0] > 0, "Market: zero amount");
            safeTransferERC20(_item.addr, _from, _to, _item.amounts[0]);
        } else {
            revert("Market: unsupported itemType");
        }
    }

    function transferMoney(address _from, address _to, uint _amount) internal {
        safeTransferERC20(address(money), _from, _to, _amount);
    }

    function safeTransferERC20(
        address _token,
        address _from,
        address _to,
        uint _amount
    )
        private
    {
        if (_from == address(this)) {
            IERC20(_token).safeTransfer(_to, _amount);
        } else {
            IERC20(_token).safeTransferFrom(_from, _to, _amount);
        }
    }
}
