contracts/HeroBank.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./AuctionHouse.sol";

contract HeroBank is Ownable, Pausable, IERC721Receiver, ERC1155Holder {
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.UintSet;

    address public immutable DFKHero;
    uint public immutable MIN_COLLATERAL;
    uint public constant BORROW_LIMIT = 6;

    AuctionHouse public auctionHouse;
    uint public auctionExpiration;

    address public operator;

    uint public maxHeroCount;
    // current hero count in bank
    uint numHeroes;
    // heroId to hero owner address
    mapping(uint256 => address) public heroOwners;
    // owner address to heroId set
    mapping(address => EnumerableSet.UintSet) ownedHeroes;

    uint numBorrows;
    uint[BORROW_LIMIT] borrowedHeroes;

    // Mapping from user address to borrow count
    mapping(address => uint) public userLoans;
    uint public totalLoans;

    event CollateralReceived(address sender, uint256 value);
    event HeroReceived(address indexed from, uint256 tokenId);
    event HeroSent(address indexed to, uint256 tokenId);
    event OperatorChanged(address newOperator, address oldOperator);
    event Claim(address indexed user, uint256 userLoans, uint256 totalLoans);

    constructor(
        uint _maxHeroCount,
        uint _minCollateral,
        address _auctionHouse,
        address _DFKHero
    ) {
        maxHeroCount = _maxHeroCount;
        MIN_COLLATERAL = _minCollateral;
        auctionHouse = AuctionHouse(_auctionHouse);
        auctionExpiration = 1 days;
        DFKHero = _DFKHero;
    }

    modifier onlyOperator() {
        require(operator == msg.sender, "HeroBank: not operator");
        _;
    }

    receive() external payable {
        emit CollateralReceived(msg.sender, msg.value);
    }

    function onERC721Received(
        address /* _operator */,
        address _from,
        uint256 _tokenId,
        bytes calldata /* _data */
    )
        external
        override
        whenNotPaused
        returns (bytes4)
    {
        if (msg.sender == DFKHero) {
            assert(heroOwners[_tokenId] == address(0));
            require(numHeroes <= maxHeroCount, "HeroBank: exceed maxHeroCount");
            onHeroReceived(_tokenId, _from);
        }
        return this.onERC721Received.selector;
    }

    function withdrawalHeroes(uint[] calldata _heroes) external {
        for (uint i = 0; i < _heroes.length; i++) {
            uint heroId = _heroes[i];
            address heroOwner = heroOwners[heroId];
            require(msg.sender == heroOwner, "HeroBank: not hero owner");
            IERC721(DFKHero).safeTransferFrom(address(this), heroOwner, heroId);
            onHeroSent(heroId, heroOwner);
        }
    }

    function createAuctionAndBid(Item calldata _item, uint256 _initialPrice) external {
        transferMoney(msg.sender, address(this), _initialPrice);
        uint auctionId = auctionHouse.create(_item, _initialPrice, auctionExpiration);
        auctionHouse.money().approve(address(auctionHouse), _initialPrice);
        auctionHouse.bid(auctionId, _initialPrice, msg.sender);
    }

    function getClaimable(address user) public view returns (uint256) {
        return getBalance() * userLoans[user] / totalLoans;
    }

    function claim() external {
        uint claimable = getClaimable(msg.sender);
        require(claimable > 0, "HeroBank: no claimable");
        uint count = userLoans[msg.sender];
        userLoans[msg.sender] = 0;
        totalLoans -= count;
        transferMoney(address(this), msg.sender, claimable);
        emit Claim(msg.sender, count, totalLoans);
    }

    function borrowHeroes(uint[] memory _heroes) external onlyOperator {
        require(numBorrows == 0, "HeroBank: unreturned heroes");
        require(_heroes.length <= BORROW_LIMIT, "HeroBank: exceed BORROW_LIMIT");
        require(address(this).balance >= MIN_COLLATERAL, "HeroBank: not enough margin");
        for (uint i = 0; i < _heroes.length; i++) {
            borrowedHeroes[i] = _heroes[i];
            IERC721(DFKHero).transferFrom(address(this), operator, _heroes[i]);
        }
        numBorrows = _heroes.length;
    }

    function repayHeroes() external onlyOperator {
        require(numBorrows > 0, "HeroBank: no borrowed hero");
        for (uint i = 0; i < numBorrows; i++) {
            uint heroId = borrowedHeroes[i];
            IERC721(DFKHero).transferFrom(operator, address(this), heroId);
            address heroOwner = heroOwners[heroId];
            userLoans[heroOwner] += 1;
            totalLoans += 1;
        }
        numBorrows = 0;
    }

    function withdrawalCollateral() external onlyOwner {
        require(numBorrows == 0, "HeroBank: unreturned heroes");
        Address.sendValue(payable(owner()), address(this).balance);
    }

    function setHeroOwner(uint _heroId, address _owner) external onlyOwner {
        require(IERC721(DFKHero).ownerOf(_heroId) == address(this), "HeroBank: not found");
        require(heroOwners[_heroId] == address(0), "HeroBank: already owned");
        onHeroReceived(_heroId, _owner);
    }

    function setAuctionExpiration(uint _expiration) external onlyOwner {
        auctionExpiration = _expiration;
    }

    function setMaxHeroCount(uint _maxHeroCount) external onlyOwner {
        maxHeroCount = _maxHeroCount;
    }

    function setOperator(address _operator) external onlyOwner {
        address oldOperator = operator;
        operator = _operator;
        emit OperatorChanged(operator, oldOperator);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function approveItem(
        address _itemAddr,
        ItemType _itemType,
        bool _approved
    )
        external
        onlyOwner
    {
        if (_itemType == ItemType.ERC20) {
            uint amount = _approved ? type(uint).max : 0;
            IERC20(_itemAddr).approve(address(auctionHouse), amount);
        } else if (_itemType == ItemType.ERC721) {
            IERC721(_itemAddr).setApprovalForAll(address(auctionHouse), _approved);
        } else if (_itemType == ItemType.ERC1155) {
            IERC1155(_itemAddr).setApprovalForAll(address(auctionHouse), _approved);
        }
    }

    function getBorrowedHeroes() public view returns (uint[] memory) {
        uint[] memory result = new uint[](numBorrows);
        for (uint i = 0; i < numBorrows; i++) {
            result[i] = borrowedHeroes[i];
        }
        return result;
    }

    function getHeroesOfOwner(address _owner) public view returns (uint[] memory) {
        EnumerableSet.UintSet storage heroes = ownedHeroes[_owner];
        uint length = heroes.length();
        uint[] memory result = new uint[](length);
        for (uint i = 0; i < length; i++) {
            result[i] = heroes.at(i);
        }
        return result;
    }

    function getBalance() public view returns (uint) {
        return auctionHouse.money().balanceOf(address(this));
    }

    function onHeroReceived(uint _heroId, address _owner) private {
        heroOwners[_heroId] = _owner;
        ownedHeroes[_owner].add(_heroId);
        numHeroes++;
        emit HeroReceived(_owner, _heroId);
    }

    function onHeroSent(uint _heroId, address _owner) private {
        delete heroOwners[_heroId];
        ownedHeroes[_owner].remove(_heroId);
        numHeroes--;
        emit HeroSent(_owner, _heroId);
    }

    function transferMoney(address _from, address _to, uint _amount) private {
        if (_from == address(this)) {
            auctionHouse.money().safeTransfer(_to, _amount);
        } else {
            auctionHouse.money().safeTransferFrom(_from, _to, _amount);
        }
    }
}

contracts/FixedPriceMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./Market.sol";

contract FixedPriceMarket is Market {
    enum OfferStatus { None, Created, Cancelled, Completed }
    struct Offer {
        address creator;
        address bidder;
        Item item;
        uint price;
        uint commission;    // in bps
        OfferStatus status;
    }

    event Created(uint indexed id, address indexed creator);
    event PriceChanged(uint indexed id);
    event Cancelled(uint indexed id);
    event Completed(uint indexed id, address bidder);

    uint public numOffers;
    mapping(uint => Offer) public offers;

    constructor(address _money, uint _commission) Market(_money, _commission) {}

    function create(
        Item calldata _item,
        uint _price
    )
        external
        whenNotPaused
        returns (uint id)
    {
        require(_price > 0, "Market: zero price");
        address creator = msg.sender;
        transferItem(creator, address(this), _item);
        id = ++numOffers;
        offers[id] = Offer({
            creator: creator,
            bidder: address(0),
            item: _item,
            price: _price,
            commission: commission,
            status: OfferStatus.Created
        });
        emit Created(id, creator);
    }

    function cancel(uint _id) external {
        address creator = msg.sender;
        Offer memory offer = offers[_id];
        require(offer.status == OfferStatus.Created, "Market: invalid offer status");
        require(offer.creator == creator, "Market: not creator");
        transferItem(address(this), creator, offer.item);
        offers[_id].status = OfferStatus.Cancelled;
        emit Cancelled(_id);
    }

    function bid(uint _id) external {
        Offer memory offer = offers[_id];
        require(offer.status == OfferStatus.Created, "Market: invalid offer status");
        address bidder = msg.sender;
        uint fee = offer.price * offer.commission / 10000;
        transferMoney(bidder, owner(), fee);
        transferMoney(bidder, offer.creator, offer.price - fee);
        transferItem(address(this), bidder, offer.item);
        offers[_id].bidder = bidder;
        offers[_id].status = OfferStatus.Completed;
        emit Completed(_id, bidder);
    }

    function changePrice(uint _id, uint _price) external {
        require(_price > 0, "Market: zero price");
        address creator = msg.sender;
        Offer memory offer = offers[_id];
        require(offer.status == OfferStatus.Created, "Market: invalid offer status");
        require(offer.creator == creator, "Market: not creator");
        offers[_id].price = _price;
        emit PriceChanged(_id);
    }
}

contracts/AutoQuest.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./DFKEarnBase.sol";

contract AutoQuest is Ownable, DFKEarnBase {
    address public constant DFKQuest = 0xAa9a289ce0565E4D6548e63a441e7C084E6B52F6;
    address public constant DFKJewelToken = 0x72Cb10C6bfA5624dD07Ef608027E366bd690048F;
    address public constant DFKGoldToken = 0x3a4EDcf3312f44EF027acfd8c21382a5259936e7;
    address public constant DFKVender = 0xe53BF78F8b99B6d356F93F41aFB9951168cca2c6;
    address public constant DFKAlchemist = 0x38e76972BD173901B5E5E43BA5cB464293B80C31;

    using EnumerableSet for EnumerableSet.AddressSet;
    EnumerableSet.AddressSet private operators;

    uint public questFee;
    uint public jewelFeeRate;
    uint public jewelFeeThreshold;
    uint public goldFeeRate;
    uint public goldFeeThreshold;
    uint public sellItemFeeRate;
    uint public sellItemFeeThreshold;

    constructor(address _accountFactory) DFKEarnBase(_accountFactory) {
        questFee = 0.12 ether;
        jewelFeeRate = 250;                 // in bps
        jewelFeeThreshold = 0.25e18;        // 0.25 jewel
        goldFeeRate = 250;                  // in bps
        goldFeeThreshold = 200e3;           // 200 gold
        sellItemFeeRate = 250;              // in bps
        sellItemFeeThreshold = 200e3;       // 200 gold
    }

    modifier onlyOperator() {
        require(operators.contains(msg.sender), "DFKEarn: not operator");
        _;
    }

    function startQuest(
        address _account,
        uint256[] calldata _heroIds,
        address _quest,
        uint8 _attempts,
        uint8 _level
    ) 
        external
        onlyOperator
    {
        chargeQuestFee(_account, _heroIds.length);
        IAccount(_account).functionCall(DFKQuest, abi.encodeWithSignature(
            "startQuest(uint256[],address,uint8,uint8)",
            _heroIds,
            _quest,
            _attempts,
            _level
        ));
    }

    function cancelQuest(address _account, uint256 _heroId) external onlyOperator {
        IAccount(_account).functionCall(DFKQuest, abi.encodeWithSignature(
            "cancelQuest(uint256)",
            _heroId
        ));
    }

    function completeQuest(address _account, uint256 _heroId) external onlyOperator {
        uint oldGoldBalance = IERC20(DFKGoldToken).balanceOf(_account);
        uint oldJewelBalance = IERC20(DFKJewelToken).balanceOf(_account);

        IAccount(_account).functionCall(DFKQuest, abi.encodeWithSignature(
            "completeQuest(uint256)",
            _heroId
        ));

        chargeGoldEarnings(_account, oldGoldBalance);
        chargeJewelEarnings(_account, oldJewelBalance);
    }

    function craftPotion(address _account, address _item, uint256 _heroId) external onlyOperator {
        IAccount(_account).functionCall(DFKAlchemist, abi.encodeWithSignature(
            "consumeItem(address,uint256)",
            _item,
            _heroId
        ));
    }

    function sellToVender(address[] calldata _items, uint[] calldata _amounts) external {
        address account = getAccount(msg.sender);
        uint256 oldGoldBalance = IERC20(DFKGoldToken).balanceOf(account);
        for (uint i = 0; i < _items.length; i++) {
            uint balance = IERC20(_items[i]).balanceOf(account);
            require(_amounts[i] <= balance, "DFKEarn: insufficient balance");
            IAccount(account).functionCall(_items[i], abi.encodeWithSignature(
                "approve(address,uint256)",
                DFKVender,
                _amounts[i]
            ));
            IAccount(account).functionCall(DFKVender, abi.encodeWithSelector(
                bytes4(0x096c5e1a),
                _items[i],
                _amounts[i]
            ));
        }
        chargeGoldEarnings(account, oldGoldBalance);
    }

    function chargeQuestFee(
        address _account,
        uint _heroCount
    )
        private
    {
        uint256 totalQuestFee = _heroCount * questFee;
        require(_account.balance >= totalQuestFee, "AutoQuest: insufficent balance");
        IAccount(_account).sendValue(payable(owner()), totalQuestFee);
    }

    function chargeGoldEarnings(address _account, uint _oldBalance) private {
        uint earnings = IERC20(DFKGoldToken).balanceOf(_account) - _oldBalance;
        if (earnings >= goldFeeThreshold) {
            uint fee = earnings * goldFeeRate / 10000;
            IAccount(_account).functionCall(DFKGoldToken, abi.encodeWithSignature(
                "transfer(address,uint256)",
                owner(),
                fee
            ));
        }
    }

    function chargeJewelEarnings(address _account, uint _oldBalance) private {
        uint earnings = IERC20(DFKJewelToken).balanceOf(_account) - _oldBalance;
        if (earnings >= jewelFeeThreshold) {
            uint fee = earnings * jewelFeeRate / 10000;
            IAccount(_account).functionCall(DFKJewelToken, abi.encodeWithSignature(
                "transfer(address,uint256)",
                owner(),
                fee
            ));
        }
    }

    function setQuestFee(uint256 _questFee) external onlyOwner {
        questFee = _questFee;
    }

    function setJewelFeeRate(uint256 _jewelFeeRate) external onlyOwner {
        jewelFeeRate = _jewelFeeRate;
    }

    function setJewelFeeThreshold(uint256 _jewelFeeThreshold) external onlyOwner {
        jewelFeeThreshold = _jewelFeeThreshold;
    }

    function setGoldFeeRate(uint256 _goldFeeRate) external onlyOwner {
        goldFeeRate = _goldFeeRate;
    }

    function setGoldFeeThreshold(uint256 _goldFeeThreshold) external onlyOwner {
        goldFeeThreshold = _goldFeeThreshold;
    }

    function setSellItemFeeRate(uint256 _sellItemFeeRate) external onlyOwner {
        sellItemFeeRate = _sellItemFeeRate;
    }

    function setSellItemFeeThreshold(uint256 _sellItemFeeThreshold) external onlyOwner {
        sellItemFeeThreshold = _sellItemFeeThreshold;
    }

    function updateOperator(address _operator, bool _add) external onlyOwner {
        if (_add) {
            operators.add(_operator);
        } else {
            operators.remove(_operator);
        }
    }

    function getOperators() public view returns (address[] memory) {
        uint length = operators.length();
        address[] memory result = new address[](length);
        for (uint i = 0; i < length; i++) {
            result[i] = operators.at(i);
        }
        return result;
    }
}

